// src/components/InviteFriend/InviteFriend.js
import React from 'react';
import './InviteFriend.css';
import { useTranslation } from 'react-i18next';

function InviteFriend() {
  const { t } = useTranslation();

  const invite = () => {
    // Логика приглашения друга через Telegram
    if (window.Telegram.WebApp) {
      window.Telegram.WebApp.MainButton.text = t('invite');
      window.Telegram.WebApp.MainButton.show();
      window.Telegram.WebApp.MainButton.onClick(() => {
        window.Telegram.WebApp.shareInviteLink();
      });
    }
  };

  return (
    <div className="invite-friend">
      <h2>{t('invite_friend')}</h2>
      <button className="invite-button" onClick={invite}>
        {t('invite')}
      </button>
    </div>
  );
}

export default InviteFriend;
